import React from "react";
import Link from "next/link";
import { Menu, Input, Button, Row, Col, Card, Avatar } from "antd";
import PropTypes from "prop-types";

const dummy = {
  nickname: "Guest"
};

const AppLayout = ({ children }) => {
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item Key="home">
          <Link href="/">
            <a>노드버드</a>
          </Link>
        </Menu.Item>
        <Menu.Item Key="profile">
          <Link href="/profile">
            <a>프로필</a>
          </Link>
        </Menu.Item>
        <Menu.Item Key="mail">
          <Input.Search
            enterButton
            style={{ verticalAlign: "middle" }}
          ></Input.Search>
        </Menu.Item>
      </Menu>
      <Link href="/Signup">
        <a>
          <Button>회원가입</Button>
        </a>
      </Link>
      <Row>
        {/* xs=작은화면일때, md=큰 화면일때 비율(24가 최대) */}
        <Col xs={24} md={6}>
          <Card actions={[]}>
            <Card.Meta
              avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
              title={dummy.nickname}
            ></Card.Meta>
          </Card>
        </Col>
        <Col xs={24} md={6}>
          {children}
        </Col>
        <Col xs={24} md={6}>
          3
        </Col>
      </Row>
    </div>
  );
};

AppLayout.PropTypes = {
  children: PropTypes.elementType // 랜더링 될 수 있는 컴포넌트들
};

export default AppLayout;
