import React, { useState } from "react";
import AppLayout from "../components/AppLayout";
import Head from "next/head";
import { Form, Input, Checkbox, Button } from "antd";
const Signup = () => {
  const [id, setId] = useState("");
  const [nick, setNick] = useState("");
  const [password, setPassword] = useState("");
  const [passwordCheck, setPasswordCheck] = useState("");
  const [term, setTerm] = useState(false);
  const [passwrodError, setpasswordError] = useState(false);
  const [termError, setTermError] = useState(false);

  const onSubmit = e => {
    e.preventDefault();
    console.log({
      id,
      nick,
      password,
      passwordCheck,
      term
    });
    if (password !== passwordCheck) {
      return setpasswordError(true);
    }
    if (!term) {
      return setTermError(true);
    }
  };
  const onChangeId = e => {
    setId(e.target.value);
  };
  const onChangeNick = e => {
    setNick(e.target.value);
  };
  const onChangePassword = e => {
    setPassword(e.target.value);
  };
  const onChangePasswordChk = e => {
    setPasswordCheck(e.target.value);
  };
  const onChangeTerm = e => {
    setTerm(e.target.checked);
  };
  return (
    <>
      <Head>
        <title>PortFolio</title>
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.26.11/antd.css"
        ></link>
        <script src="https://cdnjs.cloudflare.com/ajax/libs/antd/3.26.11/antd.js"></script>
      </Head>
      <AppLayout>
        <Form onSubmit={onSubmit} style={{ padding: 10 }}>
          <div>
            <label htmlFor="user-id">아이디</label>
            <br />
            <Input
              name="user-id"
              required
              value={id}
              onChange={onChangeId}
            ></Input>
          </div>

          <div>
            <label htmlFor="user-nick">닉네임</label>
            <Input
              name="user-nick"
              required
              value={nick}
              onChange={onChangeNick}
            ></Input>
          </div>
          <div>
            <label htmlFor="user-pass">비밀번호</label>
            <br />

            <Input
              name="user-pass"
              type="password"
              required
              value={password}
              onChange={onChangePassword}
            ></Input>
          </div>
          <div>
            <label htmlFor="user-pass-chk">비밀번호 체크</label>
            <br />

            <Input
              name="user-pass-chk"
              type="password"
              required
              value={passwordCheck}
              onChange={onChangePasswordChk}
            ></Input>
            {passwrodError && (
              <div style={{ color: "red" }}> 비밀번호가 일치하지 않습니다.</div>
            )}
          </div>
          <div>
            <Checkbox name="user-term" value={term} onChange={onChangeTerm}>
              동의합니다.
            </Checkbox>
            {termError && (
              <div style={{ color: "red" }}> 약관에 동의 해주세요.</div>
            )}
          </div>
          <div>
            <div style={{ marginTop: 10 }}></div>
            <Button type="primary" htmlType="submit">
              가입하기
            </Button>
          </div>
        </Form>
      </AppLayout>
    </>
  );
};
export default Signup;
