export const initialState = {
    isLoggedIn : false,
    user : {},
};

const LOG_IN = 'LOG_IN'; // 엑션 이름
const LOG_OUT = 'LOG_OUT';

const loginAction = {
    type: LOG_IN,
    data: {
        nickname: '송대섭'
    }
}
const logoutAction = {
    type: LOG_OUT,
  
}
const reducer = ( state = initialState, action) => {
    switch (action.type){
        case LOG_IN : {
            return {
                ...state,
                isLoggedIn : true,
                user : action.data
            }
        }
        case LOG_OUT : {
            return {
                ...state,
                isLoggedIn : false,
                user : null
            }
        }
        default: {
            return{
                ...state,
            }
        }
    } 
};

export default reducer;
