import React from "react";
import { Input, Form, Button, Card, Icon, Avatar } from "antd";
import PostForm from "../components/PostForm";

// 2-7
const Home = () => {
  return (
    <>
      <div>
       {dummy.isLoggedIn && <PostForm></PostForm> }
        
          
            {dummy.mainPosts.map(c => {
              return (
                <Card
                  key={+c.createdAt}
                  cover={c.img && <img alt="example" src={c.img}></img>}
                  actions={[
                    <Icon type="retweet" key="retweet"></Icon>,
                    <Icon type="heart" key="heart"></Icon>,
                    <Icon type="message" key="message"></Icon>,
                    <Icon type="ellipsis" key="ellipsis"></Icon>
                  ]}
                  extra={<Button>팔로우</Button>}
                >
                  <Card.Meta
                    avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                    title={c.User.nickname}
                    description={c.content}
                  ></Card.Meta>
                </Card>
              );
            })}
         
        }
      </div>
    </>
  );
};
// 2-5
export default Home;
