import React, {useEffect} from 'react';
import PropTypes from 'prop-types';
import { useDispatch, useSelector } from 'react-redux';
import { LOAD_HASHTAG_POSTS_REQUEST, LOAD_MAIN_POSTS_FAILURE } from '../reducers/post';
import PostCard from '../components/PostCard';
const Hashtag = ({tag}) => {
    console.log(tag);
    const dispatch = useDispatch();
    const { mainPosts } = useSelector(state => state.user);
    useEffect( ()=> {
        dispatch({
            type: LOAD_HASHTAG_POSTS_REQUEST,
            data: tag,
        })
    }, []);
    return(
        <div>
            {mainPosts.map(c=>(
                <PostCard key={+c.createdAt} post={c}></PostCard>
            ))}
        </div>
    );
};


Hashtag.getInitialProps = async (context) => {
    context.log('hashtag props' , context.query.tag); // 서버에서 라우팅받은 정보가 넘어오는지 확인, _app.js에서 또 추가해줌
    return { tag: context.query.tag};
};
export default Hashtag;
