import { Form,  Button, Input } from "antd";
import React, {useState, useCallback} from 'react';
import {useDispatch, useSelector} from 'react-redux';

const NicknameEditForm = () =>{
    const [editedName, setEditiedName] = useState('');
    const dispatch = useDispatch();
    const { me } = useSelector(state => state.user);
    const onChangeNickname = useCallback((e)=>{
        setEditiedName(e.target.value);
    }, []);
    const onEditNickname = useCallback((e)=>{
        e.preventDefault();
        dispatch({
            type: EDIT_NICKNAME_REQUEST,
            data: editedName,
        })
    }, []) 

    return (
        <Form style={{ marginBottom: '20px', border: '1px solid #d9d9d9', padding: '20px'}} onSubmit={onEditNickname}>
                <Input addonBefore="닉네임" value={editedName || me.nickname} onChange={onChangeNickname}></Input>
                <Button type="primary">수정</Button>
        </Form>
    )
}


export default NicknameEditForm;
