import React from "react";
import Link from "next/link";
import { Menu, Input, Button, Row, Col, Card, Avatar, Form } from "antd";
import PropTypes from "prop-types";

const dummy = {
  nickname: "Guest",
  Post: [],
  Followings: [],
  Followers: []
};

const AppLayout = ({ children }) => {
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item Key="home">
          <Link href="/">
            <a>노드버드</a>
          </Link>
        </Menu.Item>
        <Menu.Item Key="profile">
          <Link href="/profile">
            <a>프로필</a>
          </Link>
        </Menu.Item>
        <Menu.Item Key="mail">
          <Input.Search
            enterButton
            style={{ verticalAlign: "middle" }}
          ></Input.Search>
        </Menu.Item>
      </Menu>
      {/* <Link href="/Signup">
        <a>
          <Button>회원가입</Button>
        </a>
      </Link> */}
      <Row>
        {/* xs=작은화면일때, md=큰 화면일때 비율(24가 최대) */}
        <Col xs={24} md={6}>
          <Card
            actions={[
              <div key="twit">
                짹잭
                <br />
                {dummy.Post.length}
              </div>,
              <div key="twit">
                팔로잉
                <br />
                {dummy.Followings.length}
              </div>,
              <div key="twit">
                팔로워
                <br />
                {dummy.Followers.length}
              </div>
            ]}
          >
            <Card.Meta
              avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
              title={dummy.nickname}
            ></Card.Meta>
          </Card>
          <Form>
            <div>
              <label htmlFor="user-id">아이디</label>
              <br />
              <Input
                name="user-id"
                value={userId}
                onChange={onChangeId}
                required
              ></Input>
            </div>
            <div>
              <label htmlFor="user-password">비밀번호</label>
              <br />
              <Input
                name="user-password"
                value={userId}
                onChange={onChangeId}
                required
              ></Input>
            </div>
          </Form>
          <Link href="/Signup">
            <a>
              <Button>회원가입</Button>
            </a>
          </Link>
        </Col>
        <Col xs={24} md={6}>
          {children}
        </Col>
        <Col xs={24} md={6}>
          3
        </Col>
      </Row>
    </div>
  );
};

AppLayout.PropTypes = {
  children: PropTypes.elementType // 랜더링 될 수 있는 컴포넌트들
};

export default AppLayout;
